function makeNodeComponent(nodeName) {
  const func = function (props) {
    const { variants, args, overrides } = React.useMemo(
      () =>
        deriveRenderOpts(props, {
          name: nodeName,
          descendantNames: PlasmicDescendants[nodeName],
          internalArgPropNames: PlasmicVersy__ArgProps,
          internalVariantPropNames: PlasmicVersy__VariantProps
        }),
      [props, nodeName]
    );
    return PlasmicVersy__RenderFunc({
      variants,
      args,
      overrides,
      forNode: nodeName
    });
  };
  if (nodeName === "root") {
    func.displayName = "PlasmicVersy";
  } else {
    func.displayName = `PlasmicVersy.${nodeName}`;
  }
  return func;
}

